mapper.insert:
insert into notice_reply(
	nreply_id, emp_id, nreply_content, nreply_date, nreply_ref, nreply_step, nreply_level, notice_id
)
values(
	notice_reply_seq.NEXTVAL, #{emp_id}, #{nreply_content}, SYSDATE, 0, 0, 0,
	#{notice_id}
)

